mapper.update:
update course
set is_delete=1
where course_id = #{id}

mapper.select:
SELECT c.classify_id as classifyId, c.course_id as courseId, c.course_label as courseLabel, c.create_time createTime, c.details, c.img, c.banner_img as bannerImg, c.status, c.is_delete as isDelete, c.msg_type msgType, c.msg_url as msgUrl, c.pay_num as payNum, c.price, c.price_type as priceType, c.over, c.title, c.is_price as isPrice, c.title_img as titleImg, c.update_time as updateTime, cc.classification_name as classificationName, c.course_type as courseType, c.banner_id as bannerId, b.name as bannerName, c.view_counts as viewCounts, c.dy_img_id as dyImgId, c.dy_course_id as dyCourseId, c.wx_course_id as wxCourseId, c.wx_show as wxShow, c.dy_show as dyShow, c.sequence as sequence, c.is_cut as isCut, c.invite_target as inviteTarget, c.reward_money as rewardMoney, c.cut_time_limit as cutTimeLimit, c.former_name as formerName, c.producer as producer, c.score as score, c.is_external as isExternal, c.external_url as externalUrl, c.area_id as areaId, c.platform_id as platformId, (select avg(score) from course_score d where d.course_id=c.course_id ) as realScore, (select count(*) from course_details d where d.course_id=c.course_id ) as courseDetailsCount, /*(select count(*) from course_collect d where d.course_id=c.course_id ) as collectCount,*/ (select sum(good_num) from course_details d where d.course_id=c.course_id ) as goodNum, (select count(*) from course_details cd where c.course_id=cd.course_id and cd.good=1 ) as isRecommend FROM course AS c LEFT JOIN course_classification AS cc ON c.classify_id = cc.classification_id left join banner as b on b.id=c.banner_id WHERE 1 = 1 AND c.is_delete = 0 AND (c.is_external is null or c.is_external = 0)
and c.wx_show = #{wxShow}
and (c.wx_show = #{wxShow} or c.wx_show is null)
and c.dy_show = #{dyShow}
and (c.dy_show = #{dyShow} or c.dy_show is null)
and c.title = #{title}
and c.classify_id = #{classifyId}
and c.banner_id = #{bannerId}
and c.is_recommend=1
and c.over=#{over}
and c.course_id in (select course_id from course_details where good=1)
and c.status=#{status}
and c.is_price=#{isPrice}
and c.wx_course_id is not null
and c.dy_status=4
and c.is_cut=#{isCut}
order by c.create_time desc
order by goodNum desc
and is_recommend=1 order by rand()
order by c.sequence IS NULL, c.sequence asc, goodNum desc

SELECT c.classify_id as classifyId, c.course_id as courseId, c.course_label as courseLabel, c.course_label_ids as courseLabelIds, c.create_time createTime, c.details, c.img, c.banner_img as bannerImg, c.status, c.is_delete as isDelete, c.msg_type msgType, c.msg_url as msgUrl, c.pay_num as payNum, c.price, c.price_type as priceType, c.over, c.title, c.is_price as isPrice, c.title_img as titleImg, c.update_time as updateTime, cc.classification_name as classificationName, c.course_type as courseType, c.banner_id as bannerId, b.name as bannerName, c.view_counts as viewCounts, c.dy_img_id as dyImgId, c.dy_course_id as dyCourseId, c.dy_status as dyStatus, c.dy_status_content as dyStatusContent, c.dy_version as dyVersion, c.license_num as licenseNum, c.registration_num as registrationNum, c.ordinary_record_num as ordinaryRecordNum, c.key_record_num as keyRecordNum, c.wx_course_id as wxCourseId, c.wx_show as wxShow, c.dy_show as dyShow, c.sequence as sequence, c.is_cut as isCut, c.invite_target as inviteTarget, c.reward_money as rewardMoney, c.cut_time_limit as cutTimeLimit, c.former_name as formerName, c.producer as producer, c.score as score, c.is_external as isExternal, c.external_url as externalUrl, c.area_id as areaId, c.platform_id as platformId, (select avg(score) from course_score d where d.course_id=c.course_id ) as realScore, (select count(*) from course_details where dy_url_status in (1,3) or dy_url_status=null) as dyUrlStatus, (select sum(o.pay_money) from orders o where o.course_id=c.course_id and o.status=1) as payMoney, (select sum(good_num) from course_details d where d.course_id=c.course_id ) as goodNum, c.is_recommend as isRecommend FROM course AS c LEFT JOIN course_classification AS cc ON c.classify_id = cc.classification_id left join banner as b on b.id=c.banner_id WHERE 1 = 1 AND c.is_delete = 0
and c.wx_show = #{wxShow}
and (c.wx_show = #{wxShow} or c.wx_show is null)
and c.dy_show = #{dyShow}
and (c.dy_show = #{dyShow} or c.dy_show is null)
and c.title LIKE CONCAT('%', #{title}, '%')
and c.classify_id = #{classifyId}
and c.banner_id = #{bannerId}
and c.is_recommend=1
and c.over=1
and c.over is null
and c.course_id in (select course_id from course_details where good=1)
and c.status=#{status}
and c.is_price=#{isPrice}
and c.wx_course_id is not null
and c.dy_status=4
and c.is_cut=#{isCut}
order by c.create_time desc
order by goodNum desc
order by weekGoodNum desc
order by c.sequence IS NULL, c.sequence asc, goodNum desc

SELECT c.classify_id as classifyId, c.course_id as courseId, c.course_label as courseLabel, c.create_time createTime, c.details, c.img, c.banner_img as bannerImg, c.is_delete as isDelete, c.msg_type msgType, c.msg_url as msgUrl, c.pay_num as payNum, c.price, c.price_type as priceType, c.status, c.title, c.title_img as titleImg, c.update_time as updateTime, c.is_cut as isCut, FROM course c WHERE c.is_delete = 0 and c.status=1 AND (c.is_external is null or c.is_external = 0)
and c.title LIKE #{title}
order by create_time desc

SELECT c.course_id, c.title, c.producer, c.is_external, c.external_url, c.title_img, c.course_label, c.details, c.wx_show, c.dy_show, COALESCE( c.score, cs.avg_score ) AS score, cc.collect_count, cp.platform_name FROM course c LEFT JOIN ( SELECT course_id, ROUND( avg( score ), 1 ) AS avg_score FROM course_score GROUP BY course_id ) cs ON c.course_id = cs.course_id LEFT JOIN ( SELECT course_id, count( * ) AS collect_count FROM course_collect GROUP BY course_id ) cc ON c.course_id = cc.course_id LEFT JOIN course_platform cp ON c.platform_id = cp.platform_id WHERE c.is_delete = 0 AND c.STATUS = 1
and c.title LIKE CONCAT('%', #{keyword}, '%')
and c.area_Id = #{areaId}
and c.classify_Id = #{classifyId}
and c.platform_Id = #{platformId}
and c.is_Price = #{isPrice}
order by ${sortField}
asc
desc
