mapper.select:
SELECT p.*, GROUP_CONCAT(pt.ptag_id ORDER BY pt.ptag_id ASC SEPARATOR ',') AS tags, COUNT(DISTINCT pu.user_id) AS real_follower, ( COUNT( DISTINCT pu.user_id ) + p.fake_follower ) AS total_follower FROM performer p LEFT JOIN performer_ptag pt ON p.id = pt.performer_id LEFT JOIN ptag t ON pt.ptag_id = t.id LEFT JOIN performer_user pu ON p.id = pu.performer_id WHERE 1=1
AND p.name LIKE CONCAT('%',#{name},'%')
AND p.sex = #{sex}
AND p.company LIKE CONCAT('%',#{company},'%')
AND pt.ptag_id = #{tag}
GROUP BY p.id ORDER BY p.id
ASC
DESC
LIMIT #{page}, #{limit}

SELECT p.*, GROUP_CONCAT( CONCAT(pt.ptag_id,':',t.name) ORDER BY pt.ptag_id ASC SEPARATOR ',' ) AS tags, (COUNT(DISTINCT fu.user_id) + p.fake_follower) AS total_follower FROM performer p LEFT JOIN performer_ptag pt ON p.id = pt.performer_id LEFT JOIN ptag t ON pt.ptag_id = t.id LEFT JOIN performer_user fu ON fu.performer_id = p.id WHERE 1 = 1
AND pt.ptag_id = #{ptagId}
AND p.sex = #{sex}
GROUP BY p.id ORDER BY total_follower
ASC
DESC
